    function innerText(elm, text, mc)
{
    var len = 0;
    function locking()
    {
        var elm_obj = document.getElementById(elm);

        var timeoutId = setTimeout(locking, mc)

        if(text[len] != undefined) elm_obj.innerHTML += text[len];
        else clearTimeout(timeoutId)

        len++;
    }
    locking();
}
