  var x = event.pageX - posX - 25 / 2;
  var y = event.pageY - posY - 25 / 2;

  var eWave = document.getElementById('mywave');

  var newWave = eWave.cloneNode(true);
  eWave.parentNode.replaceChild(newWave, eWave);

  newWave.style.top = y + 'px';
  newWave.style.left = x + 'px';

  if(!newWave.classList.contains('mywavesactive')) {
    newWave.classList.add('mywavesactive');
  }
});

