/* First the Grid */
.gallery-grid {
  display: -ms-grid;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  grid-gap: 1.5rem;
  justify-items: center;
  margin: 0;
  padding: 0;
}

/* The Picture Frame */
.gallery-frame {
  padding: .5rem;
  font-size: 1.2rem;
  text-align: center;
  background-color: #fff;
  color: blue;
}

/* The Images */
.gallery-img {
  max-width: 100%;
  height: auto;
  object-fit: cover;
  transition: opacity 0.25s ease-in-out;
}

.gallery-img:hover {
  opacity: .7;
}

.blink {
        animation: blinker 0.6s linear infinite;
        color: #1c87c9;
        font-size: 30px;
        font-weight: bold;
        font-family: "Times New Roman", Times, serif;
      }
      @keyframes blinker {
        50% {
          opacity: 0;
        }
      }



      .grid-container {
  /*display: grid;*/
  grid-template: 130px / auto auto auto;
  grid-gap: 1px;
}

.grid-container > div {
  background-color: rgba(255, 255, 255, 0.8);
  text-align: center;
  font-size: 30px;
}

#disclaimer-cover {
  float: left;
  width: 550px;
  height: 500px;
  background: transparent
    url(https://us.123rf.com/450wm/korsana/korsana1907/korsana190700008/131877881-corner-ornament-with-ivy-shoots-frame-with-green-leaves.jpg?ver=6)
    no-repeat top left;
    background-size: 500px 500px;
}
.disclaimer-outer {
  width: 380px;
  height: 300px;
  border: none;
  padding: 0;
  overflow: hidden;
  position: absolute;
  text-align: left;
  top:550px;
  left: 15px;
  font-family: Verdana;
  font-size: 20px;
  background-color: trasparent;
  color: #000;
}
.disclaimer-inner {
  position: absolute;
  width: 100%;
  height: 900px;
  margin-top: 0px;
  text-align: left;
  /* Posizione di partenza */
  transform: translateY(100%);
  /* Applica animazione all'elemento */
  animation: scroll-up 25s linear 1s infinite normal none running;
}
/* Move it (definisce l'animazione) */
@keyframes scroll-up {
  0% {
    transform: translateY(30%);
  }
  100% {
    transform: translateY(-100%);
  }
}
/* Mette in pausa l'animazione al passaggio del mouse */
.disclaimer-inner:hover {
  cursor: pointer;
  animation-play-state: paused;
}







