function showSlides() {
  var i;
  var slides = document.getElementsByClassName("mySlides");
  var dots = document.getElementsByClassName("dot");
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";  
  }
  slideIndex++;
  if (slideIndex > slides.length) {slideIndex = 1}    
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";  
  dots[slideIndex-1].className += " active";
  setTimeout(showSlides, 2000); // Change image every 2 seconds
}


document.addEventListener('click', function(event) {
  var _this = document.querySelector('body');

  var posX = _this.offsetLeft;
  var posY = _this.offsetTop;

  var x = event.pageX - posX - 25 / 2;
  var y = event.pageY - posY - 25 / 2;

  var eWave = document.getElementById('mywave');

  var newWave = eWave.cloneNode(true);
  eWave.parentNode.replaceChild(newWave, eWave);

  newWave.style.top = y + 'px';
  newWave.style.left = x + 'px';

  if(!newWave.classList.contains('mywavesactive')) {
    newWave.classList.add('mywavesactive');
  }
});













